(function () {
  'use strict';

  $(function() {
    $('[data-page-scroller]').each(function() {
      var $element = $(this),
          target = $element.data('page-scroller');

      $element.click(function (event) {
        event.preventDefault();
        $('html, body').animate({
          scrollTop: $(target).offset().top
        }, 1.2 * 1000);
      });
      $element.css('cursor', 'pointer');
    });
  });
})();

(function () {
  'use strict';

  $(function() {
    var $readme  = $('[data-readme-formatter]');

    // guard clause
    if ($readme.length == 1) {
      // fetch sections
      var $heading   = $readme.find('[data-readme-heading]'),
          $toc       = $readme.find('[data-readme-toc]'),
          $body      = $readme.find('[data-readme-body]'),
          $expanders = $();

      // validations
      if ($heading.length !== 1) {
        console.error('must have one `data-readme-heading`');
      }

      if ($toc.length !== 1) {
        console.error('must have one `data-readme-toc`');
      }

      if ($body.length !== 1) {
        console.error('must have one `data-readme-body`');
      }

      // hide while rendering
      $readme.hide();

      // append an expander-end
      $('<div class="expander-end"></div>').appendTo($body);

      // parse headers
      $body.find('h1,h2,h3').each(function(index) {
        var $h = $(this);

        switch($h.prop('nodeName').toLowerCase()) {
          // main heading
          case 'h1':
            $h.hide().detach().appendTo($heading).fadeIn();
            break;

          // scrollable sections
          case 'h2':
            $('<li><a href="#section-' + index + '">' + $h.text() + '</a></li>')
            .click(function(event) {
              event.preventDefault();
              $('html, body').animate({
                scrollTop: $h.offset().top
              }, 1.2 * 1000);
            })
            .hide().appendTo($toc).fadeIn();
            break;

          // expandable questions
          case 'h3':
            var $a = $('<a href class="expander expander-end">' + $h.text() + '</a>');
            $h.replaceWith($a);
            $expanders.push($a);
            break;

          default:
            break;
        }

        $h.addClass('expander-end');
      });

      // set up toggling of adjacent elements between a tags
      $expanders.each(function(index) {
        var $expander = $(this),
            $children = $expander.nextUntil('.expander-end');

        $children.addClass('expandable').hide();

        $expander.click(function(event) {
          event.preventDefault();
          $children.animate({
            height: 'toggle',
            opacity: 'toggle'
          });
        });
      });

      // show it
      $readme.show();
    }
  });
})();

