(function() {
  'use strict';

  $(function() {
    if (typeof analytics === 'undefined') {
      console.error('Segment analytics is disabled.');
    } else {
      $('[data-segment-track]').each(function() {
        var $element     = $(this),
            segmentEvent = $element.data('segment-track');

        if (segmentEvent === '') {
          console.error('No Segment event specified.');

          // course trailer played
          // e.g. <a data-segment-track="Course Trailer Played" data-course-key="ud853"></a>
        } if (segmentEvent === 'Course Trailer Played') {
            var courseKey = $element.data('course-key');
            if (typeof courseKey === 'undefined') {
              console.error('No course key defined.');
            } else {
              $element.click(function() {
                analytics.track(segmentEvent, { course_key: courseKey });
              });
            }

          // degree trailer played
          // e.g. <a data-segment-track="Nanodegree Trailer Played" data-degree-key="ud853"></a>
        } if (segmentEvent === 'Nanodegree Trailer Played') {
          var degreeKey = $element.data('degree-key');
          if (typeof degreeKey === 'undefined') {
            console.error('No degree key defined.');
          } else {
            $element.click(function() {
              analytics.track(segmentEvent, { degree_key: degreeKey });
            });
          }

        } if (segmentEvent === 'Featured Moustache') {
          $element.find('a:not(.close_banner)').click(function(){
            analytics.track('CTA Clicked', { moustache_text: $element.text().trim(), moustache_link: $(this).attr('href') });
          });

          $element.children('a.close_banner').click(function(){
            analytics.track('CTA Dismissed', { moustache_text: $element.text().trim(), moustache_link: $(this).attr('href') });
          });

        } if(segmentEvent === 'Course Interested') {
            $element.click(function() {
              var $form = $(this).parents('form:first');
              var payload = {
                context: $form.find('#context').val(),
                name: $form.find('#name').val(),
                email: $form.find('#email').val()
              };
              analytics.track('Course Interested', payload);
            });
        } else { // no match on segment segmentEvent, so bind a generic segmentEvent
          var properties = $element.data('segment-properties'),
              options    = $element.data('segment-options');

          $element.click(function() {
            analytics.track(segmentEvent, properties, options);
          });
        }
      });
    }
  });
})();

