(function() {
  'use strict';

  $(document).bind('DOMContentLoaded TaskReload', function(event) {
    /**
     * define timer displayable elements
     *
     * # Example
     * <div
     *  data-timer
     *  data-time-start='2017-01-01 00:00:00'
     *  data-timer-end='2017-01-02 00:00:00'>
     * </div>
     *
     * For this example, the div element will only show at time range
     * from 2017-01-01 00:00:00 to 2017-01-02 00:00:00 for 24 hours.
     *
     */
    $('[data-timer]').each(function() {
      if (window.isTimeRange($(this).data('timer-start'), $(this).data('timer-end'))) {
        $(this).addClass('display');
      } else {
        $(this).addClass('hide');
      }
    });

    $('[data-display-uuid]').each(function() {
      var value = Cookies.get($(this).data('display-uuid'));
      if (!value) {
        $(this).slideDown();
      }
    });

    $('[data-timer-count]').each(function() {
      var now = Date.now();
      var to = new Date($(this).data('timer-count')).getTime();
      var value = $(this).text();
      var offset = to - now;
      switch ((/<%(.*)%>/.exec(value) || [])[0]) {
        case '<%d%>':
          if (offset > 0) {
            value = value.replace('<%d%>', (offset / (1000 * 60 * 60 * 24)).toFixed(0));
          } else if (offset === 0) {
            value = value.replace('<%d%>', '');
          } else {
            value = $(this).data('timer-count-end') || '';
          }
        break;
      }
      if (value) {
        $(this).text(value);
      }
    });
  });

  $(document).ready(function() {
    $('[data-hide-uuid]').on('click', function() {
      var uuid = $(this).data('display-uuid');
      Cookies.set(uuid, 'hided', {
        expires: 1
      });
    });

    /*
     * Controlable display elements depends user's authorization.
     *
     * # Example
     * <div data-show-student />
     * <div data-show-guest />
     *
     */
    if (window.getJwt()) {
      $('[data-show-guest]').addClass('displayNone');
      if ($('[data-show-student]').hasClass('displayNone')) {
        $('[data-show-student]').removeClass('displayNone');
      }
    } else {
      $('[data-show-student]').addClass('displayNone');
      if ($('[data-show-guest]').hasClass('displayNone')) {
        $('[data-show-guest]').removeClass('displayNone');
      }
    }
  });
})();

