(function() {
  'use strict';

  $(function() {

    $.fn.counterDown = function(time) {

      function _plural(i) {
        return (i + '').length === 1 ? '0' + i : i;
      }

      var format = '<span><span>dd</span><span></span></span>' +
        '<span><span>hh</span><span></span></span>' +
        '<span><span>mm</span><span></span></span>' +
        '<span><span>ss</span><span></span></span>';

      var oneDay = 1000*60*60*24;
      var oneHour = 1000*60*60;
      var oneMin = 1000*60;
      var oneSec = 1000*1;

      time = new Date(time || 0 ).getTime();
      var self = this;
      var id = setInterval(function() {
        var offset = time - Date.now() - 1000;
        // There is trick here, date.getDay will get the round number for example
        // 6.5 day will be 7 day of that month, so counter down need remove that
        // 1 day plus.
        if (offset <= 0) {
          clearInterval(id);
          return;
        }
        if (offset < oneDay) {
          format.replace('dd', '00');
        } else {
        }

        $(self).html(format.replace('dd', _plural(Math.floor(offset/oneDay)))
          .replace('hh', _plural(Math.floor((offset%oneDay)/oneHour)))
          .replace('mm', _plural(Math.floor(((offset%oneDay)%oneHour)/oneMin)))
          .replace('ss', _plural(Math.floor((((offset%oneDay)%oneHour)%oneMin)/oneSec))))
      }, 1000);
    }

  });
})();

(function() {
  'use strict';

  $(function() {

    /**
     * $(input).on('change', function() {
     *   $(this).suggest({
     *     hints: ['163.com'],
     *     default: 'qq.com',
     *   })
     * })
     *
     */

    $.fn.autocomplete = function (options) {
      options = options || {};
      var hints = options.hints || [options.default];

      var clone = $(this).clone().css({
        opacity: 0.5,
        'border-color': 'transparent',
        'box-shadow': 'none',
        position: 'absolute',
        'z-index': 0,
        'pointer-events': 'none'
      })
        .removeAttr('placeholder')
        .removeAttr('tabindex');
      $(this).css({
        'z-index': 1,
        'background-color': 'transparent'
      });
      $(this).parent().css('position', 'relative');

      $(this).after(clone);

      $(this).on('change keyup paste', function () {
        var list = [];
        var val = $(this).val();
        var hint = val;
        if (val.trim().length >= 4 && val.indexOf('@') >= 0 ) {
          var domain = val.split('@')[1];
          if (domain) {
            for (var i = 0, l = hints.length; i < l; i ++ ) {
              var v = hints[i].match(RegExp(domain + '(.*)'));
              if (v && v.length === 2) {
                hint = val + v[1];
                break;
              }
            }
          } else {
            hint = val + options.default || '';
          }
        } else {
          hint = val;
        }
        clone.val(hint);
      });

      $(this).keydown(function(evt) {
        if (evt.keyCode == 9) {
          if (clone.val() !== $(this).val()) {
            $(this).val(clone.val());
            $(this).blur();
          }
        }
      });
    }
  });
})();

